) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `lov_values`;
CREATE TABLE `lov_values` (
  `lov_value_id` int(11) NOT NULL,
  `lov_id` int(11) NOT NULL,
  `lov_value_sno` int(11) NOT NULL,
  `lov_value` varchar(100) NOT NULL,
  `lov_value_short` varchar(45) DEFAULT NULL,
  `lov_value_desc` varchar(250) DEFAULT NULL,
  `lov_valuescol` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`lov_value_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `meta_fields`;
CREATE TABLE `meta_fields` (
  `id_meta` int(11) NOT NULL AUTO_INCREMENT,
  `meta_name` varchar(45) DEFAULT NULL COMMENT 'The field name. This name is used to retrieve the display text for the field from locales files',
  `field_type` varchar(45) DEFAULT NULL COMMENT 'Type of field (values)\nText (multiple Texts)\nVarchar\nInt\nDecimal\nDate\nDateTime\n\n',
  `entity_type` varchar(45) DEFAULT NULL COMMENT 'Types of entity:\nLoan\nBorrower\nInvestor\nSystem Settings\n',
  `is_mandatory` int(11) DEFAULT '0' COMMENT 'Whether the field is mandatory or not. Default 0 (not mandatory)',
  `tab_number` int(11) DEFAULT '0' COMMENT 'If there are multiple tabs then the tab number. Default 0',
  `field_number` int(11) DEFAULT NULL COMMENT 'The field position within the tab',
  PRIMARY KEY (`id_meta`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COMMENT='Holds the definitions for the Meta Fields used by the entities\nLoans\nBorrowers\nInvestors';


DROP TABLE IF EXISTS `migrations`;
CREATE TABLE `migrations` (
  `migration` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `batch` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


DROP TABLE IF EXISTS `password_resets`;
CREATE TABLE `password_resets` (
  `email` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `token` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  KEY `password_resets_email_index` (`email`),
  KEY `password_resets_token_index` (`token`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


DROP TABLE IF EXISTS `payments`;
CREATE TABLE `payments` (
  `payment_id` int(11) NOT NULL AUTO_INCREMENT,
  `trans_date` date DEFAULT NULL,
  `trans_type` int(11) DEFAULT NULL COMMENT '1 - Investor Deposit\n2 - Borrower Payment\n3 - Borrower Repayment\n4 - Investor Repayment\n',
  `trans_amount` decimal(20,8) DEFAULT NULL,
  `currency` varchar(4) DEFAULT NULL,
  `trans_reference_number` varchar(255) DEFAULT NULL,
  `status` int(11) DEFAULT NULL COMMENT '1 Approved\n2 To be approved\n',
  `remarks` longtext,
  PRIMARY KEY (`payment_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(45) DEFAULT NULL,
  `usertype` int(11) DEFAULT NULL COMMENT 'User Type: 1 - Borrower2 - Investor3 - System user',
  `email` varchar(255) DEFAULT NULL,
  `email_verified` int(11) DEFAULT NULL,
  `firstname` varchar(100) DEFAULT NULL,
  `lastname` varchar(100) DEFAULT NULL,
  `nickname` varchar(50) DEFAULT NULL,
  `mobile` varchar(45) DEFAULT NULL,
  `password` varchar(255) DEFAULT NULL,
  `activation` varchar(300) DEFAULT NULL,
  `status` int(11) DEFAULT '2' COMMENT '1 - Active, 2 - Inactive',
  `remember_token` varchar(100) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


DROP TABLE IF EXISTS `user_challenges`;
CREATE TABLE `user_challenges` (
  `user_id` int(11) NOT NULL,
  `challenge_id` int(11) NOT NULL,
  `challenge_answer` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`user_id`,`challenge_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


